mapper.select:
select

from t_customer_trace
where customer_no = #{customerNo}
order by gmt_create desc
limit #{start},#{length}

select date_format(date, '%Y-%m') as yearMonth, count(*) as count from t_customer_trace where id in ( select a.id from ( select * from t_customer_trace where shop_no = #{shopNo} and is_valid = 1 ) a inner join ( select * from t_shop_employee where is_valid = 1
and (id = #{empNo} or manager_no = #{empNo})
) b on a.creator = b.id ) group by date_format(date, '%Y-%m') order by date_format(date, '%Y-%m') desc limit 12
