mapper.sql:
select id, title, content, point_no, join_limit, scope, shop_no,#{cond.empNo} as emp_no,'UNCOMPLETE' as status, creator, is_valid, gmt_create, gmt_modify from t_task a where ( id in (select task_no from t_task_dtl where emp_no = #{cond.empNo} and is_valid=1) or scope = 'ALL' ) and not exists (select task_no from t_task_record where emp_no = #{cond.empNo} and is_valid=1 and a.id = task_no)
a.point_no = #{cond.pointNo}
union all select id, title, content, point_no, join_limit, scope, shop_no,#{cond.empNo} as emp_no,'COMPLETE' as status, creator, is_valid, gmt_create, gmt_modify from (select id, title, content, point_no, join_limit, scope, shop_no, creator, is_valid, gmt_create, gmt_modify from t_task where is_valid = 1) m left join (select task_no from t_task_record where emp_no = #{cond.empNo} and is_valid=1) n on m.id = n.task_no where n.task_no is not null

sql.where:
is_valid = 1
and shop_no = #{shopNo}

  and gmt_create >= #{beginDate}


  and gmt_create <= #{endDate}


  and gmt_create between #{beginDate} and #{endDate}


  and id in (
    select task_no
      from t_task_dtl
      where is_valid = 1
        and shop_no = #{shopNo}
        and emp_no in (
        select id from t_shop_employee where is_valid=1 and shop_no=#{shopNo} and job_no = #{jobNo}
      )
  )

mapper.select:
select
from t_task
order by gmt_modify desc limit #{start}, #{pageSize}
